public static async Task LoginAtomics(string usuario, string senha)
{
  try
   {
    //recupera token de acesso
    using (var client = new HttpClient
    (new HttpClientHandler
    {
      AutomaticDecompression = DecompressionMethods.GZip | 
     DecompressionMethods.Deflate
    }))
    { 
      client.BaseAddress = new Uri(UrlBase);
      var data = new Dictionary();
      //adiciona os campos de usuário
      data.Add("username", usuario);
      data.Add("password", senha);
      data.Add("grant_type", "password");
      FormUrlEncodedContent form = new FormUrlEncodedContent(data);
      client.DefaultRequestHeaders.Add("Accept", "application/json");
      HttpResponseMessage resposta = await client.PostAsync("token", form);
      resposta.EnsureSuccessStatusCode();
      string result2 = await resposta.Content.ReadAsStringAsync();
      JObject objeto = JObject.Parse(result2);
      BearerToken = objeto["access_token"].ToString();
    }
  }
  catch (Exception e)
  { 
    //Sua excessão
 }
}

