public static async Task ConsultaSolicitacao(string codigoSolicitacao) {   try
   {
      if (BearerToken == null)
         throw new Exception("O token não foi recuperado. Utilize o método de login.");

      using (var client = new HttpClient(new HttpClientHandler { AutomaticDecompression 
= DecompressionMethods.Deflate | DecompressionMethods.GZip}))      {
         client.BaseAddress = new Uri(UrlBase);
         client.DefaultRequestHeaders.Add("Authorization", "Bearer " + BearerToken);
         string resposta = await client.GetStringAsync("api/v1/tipificacao/" 
         + codigoSolicitacao + "?detalhar=true&ocr=true");
         dynamic retorno = JsonConvert.DeserializeObject(resposta);
         return retorno;
      }
   }
   catch (Exception e)
   {
      //Sua excessão
   }
   return null;
}

public static async Task IncluiSolicitacao(string[] arquivos, string Legado = "55") {  try
  {
     if (BearerToken == null)
        throw new Exception("Token não foi recuperado. Utilize o método de login.");
     using (var client = new HttpClient(new HttpClientHandler {
          AutomaticDecompression = 
          DecompressionMethods.Deflate | DecompressionMethods.GZip}))
     {
        client.BaseAddress = new Uri(UrlBase);
        MultipartFormDataContent form = new MultipartFormDataContent();
        var camposEnviados = new Dictionary();

        //cabeçalho de autorização
        client.DefaultRequestHeaders.Add("Authorization", "Bearer " + BearerToken);

        //adiciona campos de entrada e 
        form.Add(new StringContent(codigoLegado), "CodigoLegado");

        foreach (string arquivo in arquivos)
        {
           byte[] dados = File.ReadAllBytes(arquivo);
           form.Add(new ByteArrayContent(dados, 0, dados.Length), 
           "arquivo", Path.GetFileName(arquivo));
        }

        HttpResponseMessage resposta;
        resposta = await client.PostAsync("api/v1/tipificacao", form);

        //recupera código da solicitação
        resposta.EnsureSuccessStatusCode();
        string codigoSolicitacao = await resposta.Content.ReadAsStringAsync();
     }
  }
  catch (Exception e)
  {
     //Sua excessão
  }
}

